import { useState } from 'react';

import { PostObjectType } from '../context/SalesItem/useContext';

export const usePostObject = (initialState: PostObjectType) => {
  const [postObject, setPostObject] = useState(initialState);

  const setPostObjectWithLocalStorage = (newPostObject) => {
    localStorage.setItem('postObject', JSON.stringify(newPostObject));
    setPostObject(newPostObject);
  };

  return [postObject, setPostObjectWithLocalStorage];
};
