import { useState } from 'react';
import * as S from './styles';

interface SecondHandItemProps {
  imageList: string[];
}

const DetailSliderPhotos = ({ imageList }: SecondHandItemProps) => {
  const [imgIndex, setImgIndex] = useState<number>(0);
  const [startX, setStartX] = useState<number | null>(null);

  const handleTouchStart = (event: React.TouchEvent<HTMLDivElement>) => {
    setStartX(event.touches[0].clientX);
  };

  const handleTouchEnd = (event: React.TouchEvent<HTMLDivElement>) => {
    const endX = event.changedTouches[0].clientX;
    if (startX && endX - startX > 50) {
      setImgIndex(imgIndex === 0 ? imageList.length - 1 : imgIndex - 1);
    } else if (startX && startX - endX > 50) {
      setImgIndex(imgIndex < imageList.length - 1 ? imgIndex + 1 : 0);
    }
    setStartX(null);
  };

  return (
    <>
      <S.SliderContainer
        onTouchStart={handleTouchStart}
        onTouchEnd={handleTouchEnd}
      >
        <S.SliderImage src={imageList[imgIndex]} />
      </S.SliderContainer>
    </>
  );
};

export default DetailSliderPhotos;
