import iconfiles from '../../assets/icons/index';
import styled from 'styled-components';
type IconName = keyof typeof iconfiles;

interface IconProps {
  name: IconName;
  width?: string;
  height?: string;
  fill?: string;
  onClick?: () => void;
}

const IconContainer = styled.div`
  display: inline-block; // 아이콘과 같은 크기의 컨테이너를 만듭니다
  box-shadow: 1px 1px 3px rgba(255, 255, 255, 0.5);
`;

const Icon = ({
  name = 'arrowUp',
  width = '24',
  height = '24',
  fill = 'black',
  onClick,
}: IconProps) => {
  const IconComponent = iconfiles[name];

  return (
    <IconContainer>
      <IconComponent
        width={width}
        height={height}
        fill={fill}
        onClick={onClick}
      />
      ;
    </IconContainer>
  );
};

export default Icon;
