const WatchListPage = () => {
  const navigate = useNavigate();
  const accessToken = localStorage.getItem(ACCESS_TOKEN);
  const { data } = useAsync(getCategory);
  const allCategories: Category[] = data?.data;
  const [myWatchList, setMyWatchList] = useState<Item[]>([]);
  const [selectedCategoryId, setSelectedCategoryId] = useState<number>(0);

  const handleCategoryChoice = (categoryId: number) => {
    setSelectedCategoryId(categoryId);
  };

  const handleItemClick = (productId: number) => {
    navigate(`${ITEM_DETAIL}/${productId}`);
  };

  const fetchWatchListData = async () => {
    const { data: fetchedWatchList } = await getWatchProducts(accessToken);
    setMyWatchList(fetchedWatchList.data);
  };

  useEffect(() => {
    fetchWatchListData();
  }, []);

  const displayedItems =
    selectedCategoryId === 0
      ? myWatchList
      : myWatchList.filter((item) => item.categoryId === selectedCategoryId);

  const watchListCategories = [
    {
      categoryId: 0,
      title: '전체',
    },
    ...allCategories.filter((category) =>
      myWatchList.some((item) => item.categoryTitle === category.title),
    ),
  ];

  return (
    <>
      <NavBarTitle type="high" centerTitle="관심 목록">
        <S.Categories>
          {watchListCategories.map((category) => (
            <S.Category
              key={category.categoryId}
              onClick={() => handleCategoryChoice(category.categoryId)}
              isActive={selectedCategoryId === category.categoryId}
            >
              {category.title}
            </S.Category>
          ))}
        </S.Categories>
      </NavBarTitle>
      {displayedItems.length ? (
        <S.ItemsContainer>
          {displayedItems.map((item) => (
            <li
              key={item.productId}
              onClick={() => handleItemClick(item.productId)}
            >
              <SecondHandItem {...item} isSetEditOption={false} />
            </li>
          ))}
        </S.ItemsContainer>
      ) : (
        <S.Empty>관심 상품이 없습니다.</S.Empty>
      )}
    </>
  );
};

export default WatchListPage;
