import { useState } from 'react';
import * as S from './styles';

interface SecondHandItemProps {
  imageList: string[];
}

// TODO : 아이템 이미지 리스트 다 가져오기

const DetailSliderPhotos = ({ imageList }: SecondHandItemProps) => {
  const [imgIndex, setImgIndex] = useState<number>(0);
  const hasNext = imgIndex < imageList.length - 1;

  const handleImgNext = () => {
    console.log('zz');
    if (hasNext) {
      setImgIndex((prevImgIndex) => prevImgIndex++);
    }
    setImgIndex(0);
  };

  console.log(imageList);
  return (
    <>
      <S.SliderContainer>
        <S.SliderImage src={imageList[imgIndex]} />
        <button onClick={handleImgNext}>클릭</button>
      </S.SliderContainer>
    </>
  );
};

export default DetailSliderPhotos;
