import * as S from './styles';

interface SelectPanelProps {
  option: string;
  mainLocationState?: boolean;
  onClickOption?: () => void | undefined;
  isLastPanel?: boolean;
  setIsOpen: Dispatch<SetStateAction<boolean>>;
}

const SelectPanel = ({
  option,
  onClickOption,
  setIsOpen,
}: SelectPanelProps) => {
  const handleClick = () => {
    if (onClickOption) {
      onClickOption();
    }
    setIsOpen(false);
  };

  return (
    <S.DropdownPanel onClick={handleClick}>
      <S.OptionTitle>{option}</S.OptionTitle>
    </S.DropdownPanel>
  );
};

export default SelectPanel;
