import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

import { ACCESS_TOKEN } from '../../constants/login';

import * as S from './styles';

import Icon from '../Icon';

interface selectSalesStatusProps {
  salesStatus: string | undefined;
  onChange: (value: string) => void;
}

const SelectSalesStatus = ({
  salesStatus,
  onChange,
}: selectSalesStatusProps) => {
  const navigate = useNavigate();
  const accessToken = localStorage.getItem(ACCESS_TOKEN);

  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen((prevIsOpen) => !prevIsOpen);
  };

  const handleChangeLocationClick = () => {
    setIsOpen(false);
    navigate(-1);
  };

  return (
    <>
      {isOpen === true && <S.DropdownWrapper onClick={toggleDropdown} />}
      <S.DropdownContainer>
        <S.DropdownHeader onClick={toggleDropdown}>
          <S.SelectedOption>{salesStatus}</S.SelectedOption>
        </S.DropdownHeader>
      </S.DropdownContainer>
    </>
  );
};

export default SelectSalesStatus;
