import styled from 'styled-components';

export const SliderContainer = styled.div`
  display: flex;
  position: relative;
  height: 491px;
  width: 100%;
`;

export const SliderTack = styled.div`
  position: absolute;
  bottom: 0px;
  margin-left: 0px;
  margin-right: 0px;
  color: white;
  width: 100%;
  height: 44px;
  display: flex;
  justify-content: space-between;
  padding: 0 16px 0 16px;
  align-items: center;
`;

export const SliderImage = styled.img`
  width: 100%;
  /* height: 100%; */
  object-fit: cover;
`;

export const Button = styled.button`
  width: 44px;
  height: 44px;
  font-size: 24px;
`;
