import styled from 'styled-components';

export const Main = styled.main`
  margin-top: 16px;
  margin-bottom: 80px;
  padding: 0 16px;
  width: 100%;
`;

export const SellerInfo = styled.div`
  display: flex;
  justify-content: space-between;
  width: 100%;
  height: 54px;
  padding: 16px;
  margin-bottom: 16px;
  background-color: #fafafa;

  :first-child {
    font-size: 15px;
    font-weight: 400;
    color: #3c3c43;
  }

  :last-child {
    font-size: 15px;
    font-weight: 400;
    color: #000000;
  }
`;
